function showHideModal(trig,targ){
    /*$(trig).on('click touchstart', function(e){
        e.preventDefault();

        console.log("show/hide modal");
        $(targ).addClass('open');
    });*/

    $(this).keydown(function (eventObject) {
        if (eventObject.which == 27){
            $(targ).removeClass('open');
        }
    });



    $('.close-modal').click(function(){
        $(targ).removeClass('open');
    });


    $(targ).on('click touchstart', function (e) {
    	console.log('close modal = ' + $(e.target).attr('class'));
        if (!$(e.target).parents().hasClass('modal-inner') && !$(e.target).hasClass('modal-inner') && !$(e.target).hasClass('btn-slider')) {
        	
            $(targ).removeClass('open');

        }

    });
}

var slideIndex = 1;

$(document).ready(function () {

    showHideModal('.open-add-modal','.modal-add');
    showHideModal('.open-edit-modal','.modal-edit');
    showHideModal('.open-delete-modal','.modal-delete');
    showHideModal('.open-share-modal','.modal-share');
    showHideModal('.open-password-update-modal','.modal-update-password');
    showHideModal('.open-new-page-modal','.modal-new-page');
    showHideModal('.open-overview-modal','.modal-overview');
    

    $('.block-stories').on( "click", '.item--expend', function(e) {

        if ($(this).hasClass('open')) {
            $(this).removeClass('open');
            $(this).parents('.item--header').removeClass('item--selected');
            $(this).find('.item--icon').addClass('icon-arrow-right');
            $(this).find('.item--icon').removeClass('icon-arrow-down');
        }
        else {
            $(this).addClass('open');
            $(this).parents('.item--header').addClass('item--selected');
            $(this).find('.item--icon').removeClass('icon-arrow-right');
            $(this).find('.item--icon').addClass('icon-arrow-down');

        }
    });

    $('.item--mark').on('click', function(e) {
        e.preventDefault();

    });

    $('.block-stories').on('click', '.open-add-modal', function (e){
        
    	e.preventDefault();

    	$('#item-name').val('');
    	
    	console.log("item name: " + $('#item-name').val());
    	
    	
        $('.content-link').removeClass('display');

        var item = $(this).data('item');
        
        console.log("item: " + item)
        
        $('.modal-add').find('.item-name').html(item);

        
        if (item == 'Chapter') {
        	var id = $(this).parents('.story-item').find('.item--id:first').text();
        	var storyItem = $(this).parents('.story-item').attr('id');
        	
        	console.log("id: " + id);
        	console.log("storyItem: " + storyItem);
        	
        	$('.modal-add').find('#addId').text(id);
        	$('.modal-add').find('#trigId').text(storyItem);
        }
        
        else if (item == 'Page') {
            $('.content-link').addClass('display');
            
            var id = $(this).parents('.chapter-item').find('.item--id:first').text();
        	var chapterItem = $(this).parents('.chapter-item').attr('id');
        	
        	console.log("id: " + id);
        	console.log("chapterItem: " + chapterItem);
        	
        	$('.modal-add').find('#addId').text(id);
        	$('.modal-add').find('#trigId').text(chapterItem);
        }
        
        console.log("show/hide modal");
        $('.modal-add').addClass('open');
        $('#item-name').focus();
    	
    });
    

    $('.block-stories').on('click', '.open-edit-modal', function (e){
        e.preventDefault();

        $('.content-link').removeClass('display');

        var item = $(this).data('item');
        var title = $(this).parents('.item--header').find('.item--title').text();

        $('.modal-edit').find('.item-name').html(item);
        $('#edit-item-name').val(title);
        
        var itemId = $(this).parents('.item:first').find('.item--id:first').text();
        $('#editId').text(itemId);

        if (item == 'Chapter') {
        	
        	var storyItem = $(this).parents('.story-item').attr('id');
        	$('.modal-add').find('#trigId').text(storyItem);
        }
        
        else if (item == 'Page') {
            $('.content-link').addClass('display');
            
        	var chapterItem = $(this).parents('.chapter-item').attr('id');
        	$('.modal-add').find('#trigId').text(chapterItem);
        }

        $('.modal-edit').addClass('open');
        $('#edit-item-name').focus();
    });

    $('.block-stories').on('click', '.open-delete-modal', function (e){

        e.preventDefault();
        var item = $(this).data('item');
        var title = $(this).parents('.item--header').find('.item--title').text();
        console.log("title:" + title);

        $('.modal-delete').find('.item-name').html(item);
        $('.delete-name').text(title);
        
        var itemId = $(this).parents('.item:first').find('.item--id:first').text();
        $('#deleteId').text(itemId);
        
        $('#pageName').text('stories');
        if (item == 'Chapter') {
        	
        	var storyItem = $(this).parents('.story-item').attr('id');
        	$('.modal-add').find('#trigId').text(storyItem);
        }
        
        else if (item == 'Page') {
            
        	var chapterItem = $(this).parents('.chapter-item').attr('id');
        	$('.modal-add').find('#trigId').text(chapterItem);
        }
        
        $('.modal-delete').addClass('open');

    });

    $('.block-stories').on('click', '.open-share-modal', function (e){

        e.preventDefault();
        
        $('#copyLink').text('Copy');
        
        var item = $(this).data('item');
        var url = $(this).data('url');
        console.log("url:" + url);

        $('.modal-share').find('.item-name').html(item);
        $('#itemLink').val(url);
        
        $('.modal-share').addClass('open');

    });
    
    $('.block-stories').on('click', '.add-page', function (e){

        e.preventDefault();
       
        var chapterId = $(this).parents('.chapter-item').find('.item--id:first').text();
    	
        console.log("chapter id: " + chapterId);
        
    	//ajax call
        var pageJson =  {'chapterId': chapterId};

        $.ajax({
            type: 'PUT',
            url: '/page/create',
            contentType: 'application/json',
            data: JSON.stringify(pageJson),
            dataType: 'json'

        }).done (function(data) {

            console.log(data);
            
            window.location.href = "/create-page/" + data.id;
            
            
        }).fail (function(err) {
            console.error(err);
        });

    });

   //CREATE PAGE
    $('#deletePage').on('click', function (e){

        e.preventDefault();
        var item = $(this).data('item');
        var title = $('#createPageTitle').text();
        console.log("title:" + title);

        $('.modal-delete').find('.item-name').html(item);
        $('.delete-name').text(title);
        
        var itemId = $('#pageId').text();
        $('#deleteId').text(itemId);
        $('#pageName').text('create-page');
        
        $('.modal-delete').addClass('open');

    });
    
    //OVERVIEW PAGE
    
    $('.block-whitebg').on('click', '.add-page', function (e){

        e.preventDefault();
       
        var chapterId = $('#chapterId').text();
        if (!chapterId) {
         chapterId = $(this).parents('.chapter-item').find('.chapter-id:first').text();
    	}
    	//ajax call
        var pageJson =  {'chapterId': chapterId};

        console.log(pageJson);
        
        $.ajax({
            type: 'PUT',
            url: '/page/create',
            contentType: 'application/json',
            data: JSON.stringify(pageJson),
            dataType: 'json'

        }).done (function(data) {

            console.log(data);
            
            window.location.href = "/create-page/" + data.id;
            
            
        }).fail (function(err) {
            console.error(err);
        });

    });
    
    $('.block-whitebg').on('click', '.open-add-modal', function (e){
        
    	e.preventDefault();

        $('.content-link').removeClass('display');

        var item = $(this).data('item');
        
        console.log("item: " + item)
        
        $('.modal-add').find('.item-name').html(item);

        $('#item-name').val('');
        if (item == 'Chapter') {
        	var id = $('#storyId').text();
        	
        	console.log("id: " + id);
        	
        	$('.modal-add').find('#addId').text(id);
        }
        
        else if (item == 'Page') {
            $('.content-link').addClass('display');
            
            var chapterId = $(this).parents('.chapter-item').find('.chapter-id:first').text();
        	
        	console.log("chapterId: " + chapterId);
        	
        	$('.modal-add').find('#addId').text(chapterId);
        }
        
        console.log("show/hide modal");
        $('.modal-add').addClass('open');
        $('#item-name').focus();
    	
    });
    
    $('.block-whitebg').on('click', '.open-edit-modal', function (e){
        e.preventDefault();

        $('.content-link').removeClass('display');

        var title = $(this).parents('.page-item').find('.excerpt-page--foot').text();

        $('.modal-edit').find('.item-name').html('Page');
        $('#edit-item-name').val(title);
        
        var pageId = $(this).parents('.page-item').find('.page-id:first').text();
        $('#editId').text(pageId);
        
        $('.content-link').addClass('display');
        
        $('.modal-edit').addClass('open');
        $('#edit-item-name').focus();
        
    });

    $('.block-whitebg').on('click', '.open-delete-modal', function (e){

        e.preventDefault();
        var title = $(this).parents('.page-item').find('.excerpt-page--foot').text();
        console.log("title:" + title);

        $('.modal-delete').find('.item-name').html('Page');
        $('.delete-name').text(title);
        
        var pageId = $(this).parents('.page-item').find('.page-id:first').text();
        $('#deleteId').text(pageId);
        
        $('.modal-delete').addClass('open');

    });
    
    
    $('.block-whitebg').on('click', '.open-overview-modal', function (e){
        e.preventDefault();
        
        var targetClassName = $(e.target).attr('class');
        console.log("target: " + targetClassName);
        if (targetClassName == "icon-edit") {
        	var id = $(this).parents('.page-item').find('.page-id').text();
        	window.location.href = "/create-page/" + id;
        }
        else if (targetClassName == "icon-del") {
        	openDeletePageModal(this);
        	
        }
        else if (targetClassName.indexOf("open-overview-modal") > 0) {
        	
        	var pageId = $(this).parents('.page-item').find('.page-id').text();
        	
        	var index = 1;
        	$('.block-whitebg--content').find('.page-item').each(function() {
        		
        		console.log($(this).find('.page-id').text() + ', ' + pageId);
        		
        		if ($(this).find('.page-id').text() == pageId) {
        			slideIndex = index;
        			return;
        		}
        		else {
        			index++;
        		}
        	});
        	
        	//slideIndex = $(this).parents('.block-whitebg--content').find('.page-item').index() + 1;
        	console.log('pageIndex: ' + slideIndex);
	        var title = $(this).parents('.page-item').find('.excerpt-page--foot').text();
	        
	        var chapterName = $(this).parents('.chapter-item').find('.chapter-name').text();
	        if (!chapterName) {
	        	chapterName = $('.block-whitebg--story-name').text();
	        }
	        var content = $(this).parents('.page-item').find('.short-text').clone();
	       
	        $(content).find('.fr-view').attr('contenteditable', 'false');
	        
	        console.log('contenteditable: ' + $(content).attr('contenteditable'));
	        console.log("title: " + title + ", content: " + $(content).html());
	        
	        $('#modal-page-title').text(chapterName + '  -  ' + title);
	        $('#modal-page-content').html(content);
	        
	        
	        $('.modal-overview').addClass('open');
        }
        
    });
    
    //ADMIN PAGE
    $('.open-password-update-modal').on('click', function (e){
        e.preventDefault();

        var id = $(this).parents('.item').find('.user-id').text();
        var userEmail = $(this).parents('.item').find('.username').text();
         
        $('#userEmail').text(userEmail);
        $('#updatePasswordId').text(id);
        
        $('.modal-update-password').addClass('open');
        $('#newPassword').focus();
        
    });
    
    $('.item').on('click', '.open-delete-user-modal', function (e){

        e.preventDefault();
        
        var userEmail = $(this).parents('.item').find('.username').text();
        console.log("userEmail:" + userEmail);
        
        $('.modal-delete').find('.item-name').text('User');
        $('.delete-name').text(userEmail);
        
        var id = $(this).parents('.item').find('.user-id').text();
        $('#deleteId').text(id);
        
        $('.modal-delete').addClass('open');

    });

});


function openNewPageModal(id) {
	console.log("id: " + id);
	
	$('#newPageId').text(id);
	
	$('.modal-new-page').addClass('open');
	
}

function openDeletePageModal(targ) {
	var title = $(targ).parents('.page-item').find('.excerpt-page--foot').text();
    console.log("title:" + title);

    $('.modal-delete').find('.item-name').html('Page');
    $('.delete-name').text(title);
    
    var pageId = $(targ).parents('.page-item').find('.page-id:first').text();
    $('#deleteId').text(pageId);
    
    $('.modal-delete').addClass('open');
}



function plusDivs(n) {
  showDivs(slideIndex += n);
}

function showDivs(n) {
  var i;
  var x = $('.block-whitebg--content').find('.page-item');
  if (n > x.length) {slideIndex = 1}    
  if (n < 1) {slideIndex = x.length}
  /*for (i = 0; i < x.length; i++) {
     x[i].style.display = "none";  
  }*/
  var page = x[slideIndex-1];
  
  console.log("page: " + page);
  
  var title = $(page).find('.excerpt-page--foot').text();
  var chapterName = $(page).parents('.chapter-item').find('.chapter-name').text();
  if (!chapterName) {
  	chapterName = $('.block-whitebg--story-name').text();
  }
  var content = $(page).find('.short-text').clone();
  
  $(content).find('.fr-view').attr('contenteditable', 'false');
  
  $('#modal-page-title').text(chapterName + '  -  ' + title);
  $('#modal-page-content').html(content);
}

